    public static void cero(int[] y){
        cadena= cadena + " 0 ";
        if (y[3]==1) {
            y[3]=0;
            
            uno(y);
        } else {
            if (y[2]==1) {
                y[2]=0;
                dos(y);
            } else{
                if (y[1]==1) {
                    y[1]=0;
                    cuatro(y);
                } else {
                    if (y[0]==1) {
                        y[0]=0;
                        ocho(y);
                    } else {
                        System.out.println("Llego a su destino 0");
                    }
                }
            }
        }
    }
