    public static int[] oper (int[] e, int[] d){
        int[] ff = new int[4];
        for (int i = 0; i < 4; i++) {
            if (e[i]==d[i]) {
                ff[i]=0;
            } else {
                ff[i]=1;
            }
        }
        return ff;
    }
